const devDiv=document.getElementById("devRepo");async function reqGitHub(){let e=await fetch("https://api.github.com/users/allankildare").then(e=>{return e.json()});const t=document.getElementById("image");t.src=e.avatar_url,t.alt=`Foto de perfil de ${e.name}`,document.getElementById("profile").href=e.html_url;const n=document.getElementById("repoData");let l=document.createElement("p");l.innerHTML=`Reposit√≥rios: ${e.public_repos}`,n.appendChild(l);let o=document.createElement("p");o.innerHTML=`Seguidores: ${e.followers}`,n.appendChild(o);let d=document.createElement("p");d.innerHTML=`Seguidores: ${e.following}`,n.appendChild(d),console.log(e)}devDiv.addEventListener("load",reqGitHub());